export default {
    state: {
       is_replay: false,
       a_step: "",
       b_step: "",
       replay_loser: "",
    },
    getters: {

    },

    mutations: {
        updateIsReplay(state, is_replay) {
            state.is_replay = is_replay;
        },

        updateSteps(state, data) {
            state.a_step = data.a_step;
            state.b_step = data.b_step;
        },

        updateRecordLoser(state, record_loser) {
            this.record_loser = record_loser;
        }
    },
    actions: {
    },
    modules: {
    }
}
