import { GameObject } from "./GameObject";
import { Wall } from "./wall";

export class GameMap extends GameObject {
    constructor(ctx, parent) {
        super();


        this.ctx = ctx;
        this.parent = parent;

        this.L = 0;

        this.rows = 13;
        this.cols = 13;

        this.walls = [];
        this.inner_wall_count = 10;
    }

    check_connectivity(g, sx, sy, ex, ey) {
        if(sx == ex && sy == ey) return true;

        g[sx][sy] = true;
        let dx = [-1, 0, 1, 0], dy =[0, 1, 0, -1];
        for (let i =0; i <4 ; i++) {
            let x = sx + dx[i], y = sy + dy[i];
            if(!g[x][y] && this.check_connectivity(g, x, y, ex, ey))
                return true;
        }
    }


    create_walls() {
        const g = [];
        for (let r = 0; r < this.rows; r++) {
            g[r] = [];
            for (let c = 0; c < this.cols; c++) {
                g[r][c] = false;
            }
        }

        //add walls for surroundings 

        for (let r = 0; r < this.rows; r++) {
            g[r][0] = g[r][this.cols - 1] = true;
        }

        for (let c = 0; c < this.cols; c++) {
            g[0][c] = g[this.rows - 1][c] = true;
        }

        //add random walls
        for(let i = 0; i < this.inner_wall_count; i++) {
            for (let j = 0; j < 1000; j++) {
                let r = parseInt(Math.random() * this.rows);
                let c = parseInt(Math.random() * this.cols);
                if(g[r][c] || g[c][r]) continue;
                if(r == this.rows - 2 && c == 1 || r == 1 && c == this.cols-2) continue;

                g[r][c] = g[c][r] = true;
                console.log(i, r, c)
                break;
            }
        }

        const copy_g = Json.parse(JSON.stringify(g));
        if(!this.check_connectivity(g, this.rows - 2, 1, 1, this.cols - 2)) return false;

        for(let r = 0; r < this.rows; r++) {
            for(let c = 0; c < this.cols; c++) {
                if(g[r][c]) {
                    this.walls.push(new Wall(r, c, this))
                }
            }
        }

        return true;
    }


    start () {
        for(let i =0; i < 1000; i++){
            if (this.create_walls()) break;
        }

    }

    update_size() {
        this.L = parseInt(Math.min(this.parent.clientWidth / this.cols, this.parent.clientHeight / this.rows));
        this.ctx.canvas.width = this.L * this.cols;
        this.ctx.canvas.height = this.L * this.rows;
    }

    update() {
        this.update_size();
        this.render();
    }

    render() {
        const color_even = "#82EAFD", color_odd = "#02BADA";
        for (let r = 0; r < this.rows; r++){
            for (let c = 0; c < this.cols; c++) {
                if ((r + c) % 2 == 0) {
                    this.ctx.fillStyle = color_even;
                } else {
                    this.ctx.fillStyle = color_odd;
                }
                this.ctx.fillRect(c * this.L, r * this.L, this.L, this.L)
            }
        }
    }
}
